insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.4.1', 'cae', '4.1.1.4.1', 'Centraliser l’information disponible dans un lieu dédié à la mobilité ou diffusée par un service unique', '<p>Ce lieu/service doit être facilement identifiable par les usagers (numéro d’appel dédié, horaires d’ouverture larges, localisation centrale…). Ce lieu/service, qui peut être mutualisé à l’échelle intercommunale, dispense des conseils gratuits et vise à réduire l’utilisation systématique de la voiture individuelle. Il s''adresse aux entreprises, aux administrations, aux commerces, aux établissements d''enseignements, de soins ou de loisirs, mais aussi aux particuliers (salariés, élèves, parents, touristes, clients...).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.4.2', 'cae', '4.1.1.4.2', 'Développer des services d’information individualisée de mobilité auprès des usagers', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.5', 'cae', '4.1.1.5', 'Travailler avec les acteurs de la mobilité à l’échelle du bassin de vie', '<p>La collectivité créé ou participe à un réseau local dédié à la mobilité durable. Ce réseau est élargi et regroupe les différents acteurs locaux concernés par la mobilité durable (collectivités, services de l''État, AOT-AOM, entreprises de transport, entreprises, organismes consulaires, associations d''usagers ou de salariés, etc.). Le réseau est destiné à capitaliser et mutualiser les bonnes pratiques ainsi que les moyens (plans de mobilité , plateforme de covoiturage, formations à l''éco-conduite, etc.). Il contribue également à la finalisation et l''adaptation de la stratégie en matière de transport et de mobilité, notamment pour l''élaboration d''un plan de mobilité ou d’un plan de mobilité simplifié  (cf. action 1.2.2).</p>
', '', '', '', '', '', null, 10.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.6', 'cae', '4.1.1.6', 'Vérifier la progression des parts modales des modes de transports alternatifs à la voiture, atteindre des valeurs exemplaires et les communiquer', '', '', '', '', '', '', null, 40.0, 'effets');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.6.1', 'cae', '4.1.1.6.1', 'Analyser la progression des parts modales dans le temps et les comparer avec les parts modales moyennes à l''échelle nationale (ou régionale pour les DOM) ou encore de collectivités exemplaires', '<p>Par exemple : part modale des piétons, des cyclistes, des TC, du co-voiturage ou autre système alternatif à la voiture individuelle, adapté au milieu rural notamment (cf. indicateurs).</p>
<p>Pour les collectivités rurales ou de petite taille, ce suivi peut-être mutualisé avec d’autres collectivités, et effectué à une échelle supérieure (avec appropriation et déclinaison par la collectivité infra).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.6.2', 'cae', '4.1.1.6.2', 'Avoir de bonnes performances', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.6.3', 'cae', '4.1.1.6.3', 'Communiquer et valoriser l''exemplarité de la politique de mobilité durable menée', '<p>via une campagne d''affichage des chiffres clés, dans les journaux locaux...</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.2', 'cae', '4.1.2', 'Promouvoir la mobilité durable en interne au sein de la collectivité', '<p>La collectivité veille à la gestion, l''utilisation efficace et à la faible consommation de carburant de sa flotte de véhicules. Cette action implique des mesures portant sur la maintenance, l''entretien, la conduite ou encore l''évolution du parc de véhicules. Il s''agit également d''optimiser, rationaliser et mutualiser l''ensemble des déplacements des agents de la collectivité :</p>
<ul>
<li>
<p>la collectivité encourage son personnel à un comportement intelligent et durable en matière de mobilité par notamment la mise en place d''un plan de mobilité employeur</p>
</li>
<li>
<p>la collectivité veille à l''utilisation efficace et à la faible consommation de carburant de sa flotte de véhicules</p>
</li>
<li>
<p>la collectivité choisit des véhicules à faibles émissions de polluants atmosphériques lors du renouvellement de sa flotte</p>
</li>
</ul>
', '<p><strong>Contexte et enjeux</strong></p>
<p>Les acteurs locaux constituent l’élément moteur d’une politique de développement durable efficace. Celle-ci appelle de nouvelles méthodes de travail et de nouveaux savoir-faire. La collectivité peut favoriser l’adhésion, l’engagement et la participation de ces acteurs par un comportement vertueux exemplaire. Les collectivités territoriales ont un devoir d’exemplarité vis-à-vis des habitants : une collectivité vertueuse peut faire figure de modèle et inciter les fonctionnaires et les citoyens, par effet d’entraînement, à s’engager également dans la voie d’un comportement plus « éco-responsable ».</p>
<p><strong>Réglementation</strong></p>
<ul>
<li>
<p>Le décret n°2020-543 du 9 mai 2020 relatif au versement du « forfait mobilités durables » dans la fonction publique de l’Etat</p>
</li>
<li>
<p>Depuis le 30 décembre 2008 (secteur privé) et le 21 juin 2010 (secteur public), les titres d''abonnement TC ou location vélo doivent être pris en charge obligatoire par l''employeur à hauteur de 50%. Les frais d''alimentation électrique d''un véhicule personnel peuvent également être pris en charge (facultatif).</p>
</li>
<li>
<p>LOTI art.27, dans le cadre des PDU réglementaires, la mise en place d''un conseil en mobilité au sein de la collectivité est obligatoire.</p>
</li>
<li>
<p>Selon la circulaire &quot;Etat exemplaire&quot; du 3 décembre 2008 actualisée par la circulaire du 2 juillet 2010 concernant la &quot;rationalisation de la gestion du parc automobile de l’Etat et de ses opérateurs&quot; : la moyenne des émissions des voitures particulières acquises ou louées par l''Etat et ses établissements publics ne doit pas dépasser 120 g de CO2/km</p>
</li>
<li>
<p>Loi LAURE précisée par article 37 loi TEPCV : les collectivités territoriales gérant une flotte &gt; 20 véhicules (poids en charge &lt; 3,5 tonnes), acquièrent ou utilisent, lors du renouvellement de leur parc automobile, au moins 20% de véhicules à faibles émissions définis comme les véhicules électriques ou les véhicules de toutes motorisations et de toutes sources d’énergie produisant de faibles niveaux d’émissions de gaz à effet de serre et de polluants atmosphériques.</p>
</li>
</ul>
', '<p><strong>Nantes</strong></p>
<p>Plan de déplacement des Agents</p>
<p>La Ville dispose depuis 2004 d''un Plan de déplacement Mutualisé avec Nantes Métropole, animé par un chef de projet dédié. Des offres variées (vélo, transports en commun) du dispositif Mobinantes+ ont permis de diminuer la part de la voiture de 8 % entre 2007 et 2012. Des enquêtes régulières sont menées afin de mesurer les effets et d''adapter les offres d''éco mobilité. A noter :</p>
<ul>
<li>
<p>Une charte du co-voitureur qui offre une attestation de covoiturage, une couverture assurance, une formation à l''éco-conduite et &quot;normalement&quot; une place réservée &quot;covoiturage”.</p>
</li>
<li>
<p>Avec la signature de la charte vélo, remise d''un kit sécurité vélo, offre d''un marquage, et offre d''une révision annuelle par un prestataire extérieur.  300 € remboursés pour l''achat d''un vélo électrique.</p>
</li>
<li>
<p>Réflexion sur les horaires d''embauche du personnel d''entretien (externalisé) de la Ville avec la signature d''une charte qui permet l’embauche à des horaires compatibles avec la circulation des transports en commun.</p>
</li>
<li>
<p>Un service de livraison de colis sur lieu de travail, mis en place sur proposition d''agents des deux collectivités afin de limiter l’impact des livraisons liées aux achats sur internet, en termes de déplacements et d’environnement, tout en offrant un service facilitant le quotidien des agents.</p>
</li>
</ul>
<p>→ Source : dossier de candidature Cit’ergie, 2015</p>
<p><strong>Nantes</strong></p>
<p>Politique d’optimisation et de développement de la part des véhicules à moindre impact dans le parc automobile</p>
<p>Le parc véhicule est mutualisé Ville, Métropole et CCAS. Il est organisé en pool avec une recherche d''optimisation (réservation avec logiciel panda qui permet l’analyse). Les règles de déplacement strictes limitent le recours à l''utilisation de véhicules dans le cadre des déplacements professionnels. Pour toute demande de remplacement de véhicule, les besoins sont estimés par un modérateur qui va aussi analyser les mutualisations possibles ou les autres alternatives. La stratégie d''achat prend pleinement en compte les émissions GES. Fin 2014, 49,2 % du parc de la Ville des véhicules &lt; 3500 kg était électriques, hybrides, GNV ou GPL.</p>
<p>→ Source : dossier de candidature Cit’ergie, 2015</p>
<p><strong>Grenoble</strong></p>
<p>Gestion de la flotte de véhicules</p>
<p>Pour réduire de 17 % la consommation énergétique du parc de la Ville et du Centre Communal d’Action Sociale, différentes actions sont mises en place de manière concomitante avec le nouveau PDA :</p>
<ul>
<li>
<p>Mise en place de badge pour un suivi fin des consommations de carburant ;</p>
</li>
<li>
<p>Retrait de tous les véhicules de services nominatifs (dont ceux des élus) ;</p>
</li>
<li>
<p>Stratégie de non renouvellement du parc administratif et de proposer une offre d’autopartage (Cité Lib) ;</p>
</li>
<li>
<p>Fin du remisage à domicile sauf exception des vélos ;</p>
</li>
<li>
<p>Renouvellement des véhicules utilitaires vers des véhicules peu émissifs : acquisition de véhicules électriques pour les services espaces verts et de la propreté urbaine (près de 60 véhicules) ;</p>
</li>
<li>
<p>Développement d’un parc au GNV ;</p>
</li>
<li>
<p>Développement d’un parc de vélos, avec mise à disposition de kits vélos et d’ateliers de réparation.</p>
</li>
</ul>
<p>→ Source : dossier de candidature Cit’ergie, 2016</p>
', '<p>ARENE IDF, « Plan de déplacement d’administration mode d’emploi », 2015
<a href="https://www.areneidf.org/publication-arene/plan-de-d%C3%A9placements-dadministration-mode-demploi">
https://www.areneidf.org/publication-arene/plan-de-d%C3%A9placements-dadministration-mode-demploi
</a></p>
', '<p>Lien avec l’action 1.2.2 : Organiser les mobilités sur le territoire</p>
<p>Ne pas évaluer les déplacements du personnel enseignant qui dépend de l''Education nationale.</p>
<p>Ne pas évaluer :</p>
<ul>
<li>
<p>les véhicules des transports publics (4.3.3)</p>
</li>
<li>
<p>les véhicules de collecte des déchets (1.2.3)</p>
</li>
</ul>
<p>Articulation ville-EPCI : Même notation ville et agglomération lorsque les services sont mutualisés.</p>
<p>Labellisation unique ville et EPCI : Indicateurs communs additionnant les données véhicules et agents des deux collectivités.</p>
', '<p>Réductions de potentiel cumulables :</p>
<ul>
<li>
<p>Moins 20% si la desserte des locaux de la collectivité par les transports publics est inenvisageable (absence totale de transport en commun)</p>
</li>
<li>
<p>Moins 30% si la collectivité ne dispose pas de véhicules (et ne pas traiter les items sur ce sujet)</p>
</li>
</ul>
', 6.0, null, null);
