insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.2.1.1', 'cae', '4.1.2.1.1', 'Evaluer ses besoins en matière de véhicules motorisés', '<p>en termes de kilomètres parcourus, usages, temps et fréquence d''utilisation, location, communication…</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.2.1.2', 'cae', '4.1.2.1.2', 'Réaliser un bilan économique de son poste « déplacements »', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.2.1.3', 'cae', '4.1.2.1.3', 'Réaliser un diagnostic de la flotte de véhicules', '<p>A ce titre, la collectivité élabore :</p>
<ul>
<li>
<p>un carnet de suivi pour chaque véhicule qui permettra notamment de classer les véhicules en fonction de leur impact sur le climat et l''énergie. Le carnet de suivi comprend le modèle, le type, l''âge, la catégorie, le service de rattachement, le nombre de kilomètres parcourus annuellement, le type de carburant, la consommation de carburant, les émissions de CO2 et de GES, la norme Euro, le budget global ;</p>
</li>
<li>
<p>un tableau de bord de suivi des consommations de carburants (global, par véhicule et par service) et des émissions de GES associées et de pollutions atmosphériques (a minima oxydes d’azote et particules) ;</p>
</li>
<li>
<p>un bilan des sous-produits (carburants, huiles, pneus, produits d''entretien, pièces de rechange, etc.) ;</p>
</li>
<li>
<p>un bilan des entretiens sur le véhicule.</p>
</li>
</ul>
', '', '', '', '', '', null, null, null);
