insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.2.2.1', 'cae', '4.1.2.2.1', 'S’engager politiquement en faveur d’un Plan de Mobilité', '<p>Avoir une délibération sur des objectifs chiffrés, discussion sur la mise en place du Forfait Mobilités durables.</p>
<p>Pour les collectivités avec moins de 50 agents, moins de formalisme est exigé (parler de programme d’actions).</p>
', '', '', '', '', '', null, null, null);
