insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.2.2.5', 'cae', '4.1.2.2.5', 'Intégrer dans les directives d’achats des critères d’efficacité énergétique et des carburants faiblement émetteurs en CO2 et polluants atmosphériques', '<p>Une attention portée à l’étiquette énergie, au poids des véhicules ainsi qu’à leur motorisation : électrique, biogaz ou hydrogène</p>
', '', '', '', '', '', null, null, null);
