insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.2.3.1', 'cae', '4.1.2.3.1', 'Proposer une offre de formation de type "Eco-drive" aux agents', '<p>Ces formations sont proposées à tous les agents, et le suivi de ces formations se fait notamment pour les collaborateurs parcourant beaucoup de kilomètres, via des cours réguliers de remise à niveau (l''administration paie les frais de formation et les heures d''absence).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.2.3.2', 'cae', '4.1.2.3.2', 'Renouveler la flotte de véhicules', '<p>Renouveler en priorité les véhicules les plus polluants et énergivores et acheter de nouveaux véhicules en s''appuyant sur les critères fixés en matière d''énergie, d''émissions de carbone et de polluants ainsi qu''en prenant en compte les besoins et usages de la collectivité (taille, poids des véhicules...)</p>
', '', '', '', '', '', null, null, null);
