insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.2.5.3', 'cae', '4.1.2.5.3', 'Encourager le télétravail et les vidéo-conférences', '<p>Si le débit n''est pas suffisant pour ces systèmes, alors la collectivité est &quot;non concernée&quot;.</p>
