insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.2.5.4', 'cae', '4.1.2.5.4', 'Aménager les horaires pour faciliter l’usage des modes actifs ou TC', '<p>En l’absence de TC, la collectivité mène une réflexion pour mettre en place des systèmes de mobilités alternatives aux TC par exemple ligne de bus scolaire ou vélo-bus scolaire, transport à la demande, réflexion autour des pistes cyclables touristiques, etc.</p>
', '', '', '', '', '', null, null, null);
