insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.2.2.2.2', 'cae', '4.2.2.2.2', 'Mettre en place un outil de suivi du trafic (en temps réel dans l’idéal) et utiliser des données pour l’amélioration de la politique de mobilité et notamment sur les évolutions des tracées des voies bus (pour les collectivités traversées par des flux importants et/ou celles dotées de transport en commun)', '', '', '', '', '', '', null, null, null);
