insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.2.2.3', 'cae', '4.2.2.3', 'Réaménager et restructurer la voirie et les espaces publics afin de créer des zones de circulation apaisée', '', '', '', '', '', '', null, 35.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.2.2.3.1', 'cae', '4.2.2.3.1', 'Réaménager et restructurer la voirie et les espaces publics (trame viaire, organisation et signalétique) dans les zones présentant des problématiques de conflits (zones hostiles) piétons/cyclistes/automobilistes', '<p>Avec pour objectif d''améliorer la qualité des espaces publics et la redynamisation du commerce de proximité :</p>
<ul>
<li>
<p>Conception des espaces publics agréable, continue, accessible aux personnes à mobilité réduite et sécurisée d''itinéraires piétonniers et cyclistes, aménagement d''espaces publics</p>
</li>
<li>
<p>Créer une ou plusieurs Zones à Circulation Restreinte en zone PPA</p>
</li>
<li>
<p>Créer une ou plusieurs zones de rencontre (permet à la rue de retrouver la diversité de ses usages, en redonnant la priorité au piéton. La différence avec la zone piétonne est la présence de véhicules motorisés. Mais le piéton bénéficie de la priorité sur tous (sauf le tramway) sur toute la largeur de la voie, même la chaussée. La vitesse des véhicules est limitée à 20 km/h, et le stationnement impossible sur ces voies.)</p>
</li>
<li>
<p>Créer une ou plusieurs zones 30 (zone de circulation qui se veut apaisante puisqu’elle limite à 30 km/h la vitesse des véhicules et rassure les autres usagers de la voirie (piétons et cyclistes)).</p>
</li>
<li>
<p>Végétalisation et création d''espaces verts et plantation dans les zones apaisées</p>
</li>
</ul>
', '', '', '', '', '', null, null, null);
