insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.3.2.1', 'cae', '4.3.3.2.1', 'Se rapprocher des AOM (autorités organisatrices de la mobilité) locales afin d''évaluer les besoins des usagers actuels', '<p>Celles-ci pourront fournir des éléments de diagnostic concernant : l''usage des transports collectifs (fréquentation des points d''arrêt en semaine et le week-end, origine et destination des usagers, types de titres de transport utilisés, difficultés rencontrées (en particulier sur les heures de pointe), l''offre de transport (type et nombre de véhicules en exploitation), les éléments contractuels (type de conventionnement, date du prochain conventionnement, montant de la subvention d''équilibre, recettes tarifaires), l''historique des éventuels dysfonctionnements (horaires non ajustés, retards, sous-capacité ou surcapacité des véhicules), la fiabilité lors d''événements climatiques extrêmes.</p>
', '', '', '', '', '', null, null, null);
