insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.3.4.1', 'cae', '4.3.3.4.1', 'Optimiser l''offre existante', '<p>Amélioration de la qualité de service – amplitude et couverture du territoire, cadencement et augmentation des fréquences, du maillage, ajustement de la localisation des arrêts, adaptation de la desserte, confort des véhicules, en associant les AOM (des indicateurs chiffrés attestent de la qualité de l’offre).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.3.4.2', 'cae', '4.3.3.4.2', 'Donner la priorité aux transports publics dans le trafic, en lien avec les collectivités compétentes en matière de voirie', '<p>par exemple : voies en site propre, commande de la signalisation, priorité aux feux, lors d''événements climatiques extrêmes… (l''évolution des vitesses commerciales peut en attester)</p>
', '', '', '', '', '', null, null, null);
