', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.3.4.4', 'cae', '4.3.3.4.4', 'Prendre des mesures pour assurer la fiabilité lors d''événements climatiques extrêmes, et la qualité des transports', '<p>En particulier le confort thermique et olfactif dans les transports en commun, en hiver et en été (ex : ombrage des stations de remisage).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.3.4.5', 'cae', '4.3.3.4.5', 'Associer les principaux générateurs de déplacements à la réflexion, travailler en amont avec eux afin de les sensibiliser et les inciter à ajuster leur organisation et leurs horaires', '<p>Sans perdre de vue les éventuels risques d''une saturation du réseau de transports collectifs. La sensibilisation de ces acteurs porte notamment sur les enjeux associés à la mobilité et sur les bénéfices qu''ils peuvent en tirer (amélioration de la ponctualité des salariés, diminution de la fatigue, etc.).</p>
', '', '', '', '', '', null, null, null);
