insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.3.7.1', 'cae', '4.3.3.7.1', 'Évaluer la progression de l''utilisation des transports publics', '<p>Enquêtes périodiques montrant une évolution favorable de la fréquentation des transports en commun : voyage/hab.an, nb d’usagers… (cf. indicateurs)</p>
<p>Sondages de popularité, sentiment de sécurité, respect des horaires, etc.</p>
', '', '', '', '', '', null, null, null);
