insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.1.2.1', 'cae', '4.3.1.2.1', 'Identifier les zones à enjeux importants en matière de mobilité piétonne (centre, zones résidentielles) et mettre en place des mesures (aménagement et signalétique) visant à l''apaisement de celles-ci (zones limitées à 30 km/h, zone de rencontres…)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.1.2.2', 'cae', '4.3.1.2.2', 'Repérer les points noirs pouvant freiner le développement de la mobilité piétonne dans certaines zones et les traiter', '<p>Aménager et sécuriser davantage de passages piétons, les abords des écoles, supprimer le maximum de stop et de feux tricolores en zone apaisée et rétablir la priorité à droite</p>
', '', '', '', '', '', null, null, null);
