insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.1.3.2', 'cae', '4.3.1.3.2', 'S''appuyer sur la signalisation afin d''accompagner et guider les piétons', '<p>Accompagnement des piétons : jalonnement directionnel indiquant les temps d’accès, signalisation spécifique, commande des feux de signalisation favorable aux piétons, aide pour la traversée des routes principales, déblayage de la neige sur les chemins piétons en hiver, ombrage pour le confort d''été...</p>
', '', '', '', '', '', null, null, null);
