insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.1.4', 'cae', '4.3.1.4', 'Devenir un territoire exemplaire en matière de mobilité piétonne', '<p>La généralisation de la zone 30 en ville est également valorisée dans l’action 4.2.2 « Réguler le trafic, réduire la vitesse et valoriser l''espace public ».</p>
', '', '', '', '', '', null, 30.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.1.4.1', 'cae', '4.3.1.4.1', 'Se saisir des outils réglementaires, avec des scénarios ambitieux (large périmètre géographique, suffisamment de catégories de véhicules visés par les restrictions…) et des moyens de contrôle (respect par les usagers)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.1.4.2', 'cae', '4.3.1.4.2', 'Créer une ou plusieurs aires piétonnes', '<p>C’est une zone dédiée où seuls les tramways ont la priorité sur les piétons. Les cyclistes et les véhicules doivent rouler au pas. La circulation des véhicules motorisés est exceptionnelle, et conditionnée à la desserte de la zone, de même que le stationnement, y compris pour les résidents. Un accès temporaire à la zone piétonne peut être maintenu pour les véhicules motorisés des riverains et les livreurs qui disposent d''un badge pour passer les bornes automatiques.</p>
', '', '', '', '', '', null, null, null);
