insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.1.3.2', 'cae', '1.1.3.2', 'Connaître la vulnérabilité du territoire au changement climatique', '<p>Cette étude justifie des entretiens avec les acteurs du territoire (secteurs du bâtiment, de la santé, du tourisme, de l’agriculture et de la forêt, syndicats et gestionnaires d’infrastructures et réseaux d’eau, d’énergie, de transport, etc.) et n’est pas seulement réalisée à partir de bibliographie.</p>
<p>Les impacts du changement climatique sur le territoire, observés et potentiels, sont hiérarchisés (sensibilité X exposition).</p>
', '', '', '', '', '', null, 10.0, 'mise en œuvre');
