insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.1.1.5', 'cae', '1.1.1.5', 'Décliner la stratégie climat-air-énergie de manière opérationnelle en cohérence avec les objectifs', '<p>La stratégie climat-air-énergie est déclinée clairement de manière opérationnelle, en cohérence avec les objectifs, pour chaque axe : actions et sous-tâches / répartition des responsabilités entre les acteurs internes et/ou externes à la collectivité (services et noms identifiés) / calendrier / budget / indicateur de suivi</p>
