insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.1.2', 'cae', '1.2.4.1.2', 'Prendre en compte les enjeux environnementaux dans le programme local de l''habitat (PLH)', '<p>La maîtrise des déplacements, la limitation de la consommation énergétique et des ressources naturelles (parmi lesquelles le foncier), la qualité de l’air (intérieur et extérieur) sont bien présentes dans le PLH (ou équivalent) à côté des préoccupations sociales ou économiques traditionnelles.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.1.3', 'cae', '1.2.4.1.3', 'Intégrer dans le diagnostic du Programme Local de l''Habitat (ou équivalent) des éléments sur la performance énergétique du parc bâti', '<p>Collecte de données locales permettant de réaliser une cartographie du parc bâti par type de bâtiment, enquête sur les travaux de rénovation effectués, etc.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.1.4', 'cae', '1.2.4.1.4', 'Indiquer la rénovation thermique de l''habitat comme un objectif prioritaire du Programme Local de l''Habitat', '<p>La rénovation thermique est inscrite dans le programme local de l''habitat (ou équivalent) ou dans d’autres programmes (ex : PIG, convention SARE (espace conseil FAIRE)) et projets (ex : projets ANRU – Agence Nationale pour la Rénovation Urbaine, projets de territoires d’un PETR).</p>
<p>Cet enjeu est identifié spécifiquement et non noyé dans le document.</p>
', '', '', '', '', '', null, null, null);
