insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.2.1', 'cae', '1.2.4.2.1', 'Disposer d''un diagnostic sur l''état du parc bâti tertiaire du territoire', '<p>Le diagnostic inclut les informations recueillies dans le cadre du dépôt de dossier technique répondant au décret tertiaire.</p>
