insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.2.2', 'cae', '1.2.4.2.2', 'Disposer d''un document cadre visant à qualifier/inciter la construction et la rénovation performante des locaux tertiaires', '<p>Ce document peut être remplacé par un volet clairement identifié dans un autre document de planification.</p>
