insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.3.2', 'cae', '1.2.4.3.2', 'Limiter l’étalement urbain de la politique de l’habitat et du tertiaire', '<p>Fixer un nombre maximum de nouveaux logements (ou de surfaces de locaux tertiaires/commerciaux), encadrer leur typologie (collectif, individuel, intermédiaire) et leur localisation (% de logements/locaux tertiaire dans l’enveloppe urbaine, % en renouvellement, % en extension…).</p>
<p>Fixer des densités planchers différenciées selon les secteurs.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.3.3', 'cae', '1.2.4.3.3', 'Définir des zones préférentielles de développement dans le Programme Local de l''Habitat cohérentes avec la mobilité durable', '<p>Le PLH (ou équivalent) définit des zones préférentielles de développement dans des zones bien desservies par les transports en communs et/ou dans des zones au trafic apaisé favorisant les modes actifs de préférence à la voiture et/ou dans des zones où l’exposition aux polluants atmosphériques est minimale ; ou facilitant le recours aux énergies renouvelables ou de récupération, tout en prenant en compte l''exposition aux polluants atmosphériques.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.3.4', 'cae', '1.2.4.3.4', 'Contextualiser et prioriser la politique de la collectivité par rapport aux enjeux forts/spécifiques du territoire', '<p>Exemples d''enjeux forts/spécifiques du territoire : habitat social, copropriétés, résidences secondaires, revitalisation des centres-bourg, lutte contre la vacance, bâti à caractère patrimonial, etc.</p>
', '', '', '', '', '', null, null, null);
