insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.4.4', 'cae', '1.2.4.4.4', 'Viser l''amélioration de la qualité de l''air intérieur des bâtiments', '<p>Par exemple avec des exigences ou recommandations sur les systèmes de ventilation ou des recommandations sur les matériaux utilisés.</p>
