insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.1.2', 'cae', '1.2.1.1.2', 'Déléguer la compétence schéma directeur des énergies et des réseaux de chaleur à l’intercommunalité', '<p>Déléguer si possible la compétence à l’intercommunalité pour gérer de manière plus coordonnée et cohérente l’aménagement du territoire et le développement des énergies renouvelables</p>
', '', '', '', '', '', null, null, null);
