insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.2.1', 'cae', '1.2.1.2.1', 'Étudier la faisabilité des réseaux de chaleur alimentés en énergies renouvelables et de récupération', '<p>A minima pour les bâtiments et équipements publics et/ou étudier la faisabilité d''un raccordement pour tous les bâtiments publics situés à moins de 500 mètres d’un réseau de chaleur existant majoritairement alimenté par des énergies renouvelables et de récupération (EnR&amp;R).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.2.2', 'cae', '1.2.1.2.2', 'Élargir la réflexion en menant une étude de faisabilité de création d’un réseau de chaleur alimentant les bâtiments publics et privés.', '<p>En l’absence d’une densité énergétique suffisante (densité énergétique préconisée supérieure à 1,5 MWh/ml, source : ADEME), cette étude n’est pas nécessaire.</p>
', '', '', '', '', '', null, null, null);
