insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.3.1', 'cae', '1.2.1.3.1', 'Prendre en compte et quantifier tous les potentiels de développement', '<p>Les études sont orientées selon les spécificités du territoire (exemple enjeu du chauffage au bois individuel en territoire rural).</p>
<p>La multiplication des projets est encouragée.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.3.2', 'cae', '1.2.1.3.2', 'Établir un plan d''approvisionnement territorial en bois énergie (collectivités métropolitaines)', '<p>Permet de connaître la disponibilité en bois énergie, la demande spécifique au territoire, ainsi que les consommateurs potentiels, pour organiser localement l''approvisionnement des chaufferies bois, tout en limitant les risques de conflit d''usage.</p>
', '', '', '', '', '', null, null, null);
