insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.4.2', 'cae', '1.2.1.4.2', 'Prévoir l''extension du réseau de chaleur et l''évolution vers un mix énergétique supérieur à 50 % d’énergies renouvelables', '<p>En présence d’un réseau de chaleur existant, l''extension du réseau de chaleur est prévue ainsi que l''évolution vers un mix énergétique supérieur à 50 % d’énergies renouvelables (si ce mix n''est pas encore atteint).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.5', 'cae', '1.2.1.5', 'Déterminer les secteurs géographiques prioritaires de développement des énergies renouvelables et de récupération', '<p>Les secteurs de développement déterminés permettent :</p>
<ul>
<li>
<p>une utilisation majoritaire des rejets thermiques disponibles et des énergies renouvelables produites localement, avec indication des conséquences (notamment sur la qualité de l''air, en lien avec l''analyse des impacts du changement climatique, à la fois sur la production, hydraulique par exemple, et sur les besoins, en été par exemple),</p>
</li>
<li>
<p>le développement de réseaux de chaleur, de froid, etc, en distinguant les zones propices aux créations de nouveaux réseaux et/ou les zones favorables à la densification et aux extensions (densité énergétique préconisée supérieure à 1,5 MWh/ml, source : ADEME).</p>
</li>
</ul>
', '', '', '', '', '', null, 20.0, 'mise en œuvre');
