insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.6.3', 'cae', '1.2.1.6.3', 'Intégrer des mesures de maîtrise de la demande en énergie', '<p>Intégrer des mesures de maîtrise de la demande en énergie afin de dimensionner et localiser les installations de façon cohérente vis-à-vis des bâtiments ayant vocation à devenir moins consommateurs d’énergie qu’aujourd’hui.</p>
<p>(ou prendre en compte les mesures prévues à ce sujet sur le territoire, notamment dans la mesure 1.2.4
Organiser et planifier l’augmentation des performances climat-air-énergie des bâtiments résidentiels et tertiaires
et 6.2.2
Prévenir et lutter contre la précarité énergétique
)</p>
', '', '', '', '', '', null, null, null);
