insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.7.1', 'cae', '1.2.1.7.1', 'Mettre à disposition de l’ensemble des parties prenantes du territoire la programmation énergétique', '<p>La programmation énergétique mise à disposition comprend études de potentiel, de faisabilité et de développement des ENR pour une appropriation et déclinaison des objectifs à une échelle inférieure (commune, entreprise, particulier, etc.)</p>
<p>Un interlocuteur est désigné, capacité à présenter la stratégie, etc.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.7.2', 'cae', '1.2.1.7.2', 'Soigner la portée et la forme des études réalisées', '<p>Une restitution sous forme de cartes est privilégiée pour donner à voir les possibilités de développement des différentes filières sur chaque secteur.</p>
', '', '', '', '', '', null, null, null);
