insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.2.5.1', 'cae', '1.2.2.5.1', 'Intégrer des mesures organisationnelles et de suivi', '<p>Présence d''un tableau de bord indiquant le calendrier, le budget et les acteurs prévus pour chaque action, les gains estimés en termes de GES et de polluants atmosphériques</p>
