insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.3.2.1', 'cae', '1.2.3.2.1', 'Engager des actions allant au-delà des obligations réglementaires et mobiliser les acteurs pour réduire, réutiliser, réparer, recycler, valoriser les ressources/les déchets', '<p>A partir d’un diagnostic sur le territoire (flux, coûts, acteurs, actions de prévention déjà engagées, identification des gisements de déchets, définition des potentiels de réduction, mobilisation et coopération des acteurs, etc.), complété d’études de faisabilité, engager des actions allant au-delà des obligations réglementaires et mobilisant les acteurs (habitants, acteurs publics, acteurs économiques, associations, etc.) pour réduire, réutiliser, réparer, recycler, valoriser les ressources/les déchets en veillant à limiter les gaz à effet de serre et la pollution atmosphérique.</p>
', '', '', '', '', '', null, null, null);
