insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.3.2.22', 'cae', '1.2.3.2.22', 'Prendre en compte l''objectif national de recycler 70% des déchets du BTP et mettre en œuvre des actions pour y contribuer', '<p>A minima pour les chantiers publics (taux de recyclage supérieur ou égal à 70 %) et de manière plus large, participation de la collectivité à la coordination/mise en place de la filière privée de collecte et de valorisation des déchets du BTP (identification de sites propices, facturation d''un service de reprise de déchets dans les déchetteries publiques selon les capacités d''accueil, contrat entre collectivités et distributeurs...).</p>
