insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.3.3.1', 'cae', '1.2.3.3.1', 'Produire moins de déchets ménagers et assimilés que la moyenne nationale, voire moins que les territoires pionniers en France', '<p>Produire moins de déchets ménagers et assimilés que la moyenne nationale (573 kg/hab.an) et se comparer par rapport aux territoires pionniers en France (480 kg/hab.an) (DMA avec déblais et gravats)</p>
