', '', '', '', '', '', null, 20.0, 'bases');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.2.1', 'cae', '1.3.1.2.1', 'Réaliser des analyses croisées des enjeux climat-air-énergie et urbains', '<p>Réalisation d''analyses croisées des enjeux climat-air-énergie et urbains, de type AEU2 ou utilisation d''outils d''aide à la décision comme GES-Urbanisme (CEREMA) ou d''outils de modélisation de l''impact de la politique d''urbanisme sur la qualité de l''air pour l''élaboration des documents d''urbanisme (PLU, SCOT) et sur les secteurs à enjeux pour les traduire en orientations d''aménagement opposables</p>
', '', '', '', '', '', null, null, null);
