insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.2.3', 'cae', '1.3.1.2.3', 'Intégrer dans le rapport de présentation du PLU/SCOT un état des lieux de la situation en matière d’émissions de gaz à effet de serre, de consommations et productions énergétiques, d’émissions de polluants atmosphériques, des vulnérabilités climatiques', '<p>En complément des thématiques réglementaires (habitat, déplacement, etc.) et en lien étroit avec les données disponibles dans les autres documents de planification (notamment PDM, PLH, PCAET), le rapport de présentation du PLU/SCOT comporte un état des lieux de la situation en matière d’émissions de gaz à effet de serre, de consommations et productions énergétiques, d’émissions de polluants atmosphériques, des vulnérabilités climatiques (exemple : état des lieux des quartiers les plus touchés par la chaleur estivale)</p>
', '', '', '', '', '', null, null, null);
