insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.3.1', 'cae', '1.3.1.3.1', 'Intégrer dans le PADD des engagements clairs pour l''adaptation et l''atténuation par rapport au changement climatique', '<p>Le plan d''aménagement et de développement durable (PADD) comporte des engagements clairs pour l''adaptation et l''atténuation par rapport au changement climatique (ces engagements sont si possible spatialisés et chiffrés).</p>
<p>Par exemple en matière de réseaux d’énergie pour favoriser le développement des réseaux de chaleur alimentés majoritairement par des énergies renouvelables et de récupération (ENRR) et pour coordonner les différents réseaux d''énergies, ou en matière de trame verte et bleue pour optimiser globalement la résistance et la résilience vis-à-vis des évènements climatiques extrêmes).</p>
', '', '', '', '', '', null, null, null);
