insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.4.4', 'cae', '1.3.1.4.4', 'Disposer de documents cartographiques déterminants mettant en évidence les secteurs pouvant faire l''objet de prescriptions énergétiques ou urbaines particulières, cartes stratégiques air, trame verte et bleue', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.4.5', 'cae', '1.3.1.4.5', 'Contribuer explicitement dans les  règlement du PLU et le DOO du SCOT (en dictant certaines règles au PLU le cas échéant) à la lutte et à l''adaptation au changement climatique, ainsi qu''à la préservation de la qualité de l''air', '<p>En favorisant notamment : la compacité des formes urbaines, les économies d''énergie et la diversification des sources d''énergie, la végétalisation et la gestion locale des eaux pluviales, la limitation de la voiture, la mixité fonctionnelle.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.4.6', 'cae', '1.3.1.4.6', 'Utiliser les nouvelles possibilités Grenelle, ALUR et TECV', '<p>Exigences énergétiques et environnementales renforcées dans certains secteurs du PLU/SCOT, bonification de gabarit sur critères de performance énergétique, densité minimale de construction dans les secteurs bien desservis pas les TC, à proximité des réseaux de chaleur alimentés majoritairement par des ENRR et/ou à proximité d''équipements collectifs aux besoins importants d''énergie, coefficient de biotope par surface, normes maximales de stationnement motorisés, etc.</p>
', '', '', '', '', '', null, null, null);
