insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.5.4', 'cae', '1.3.1.5.4', 'Limiter l’artificialisation des sols sur le long terme, avec la mise en place de dispositifs réglementaires de protection et d’outils d''intervention foncière', '<p>Outils d''intervention foncière : droit de préemption de la SAFER, droit de préemption urbain des communes/EPCI, PAEN, zones agricoles protégées, ENS, zone d’aménagement différé, zone agricole protégée.</p>
', '', '', '', '', '', null, null, null);
