insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.2.1', 'cae', '1.3.2.2.1', 'Demander une approche intégrée de type AEU2 pour une analyse croisée des enjeux climat-air-énergie et urbains pour tous les projets d''urbanisme opérationnel maîtrisés par la collectivité', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.2.2', 'cae', '1.3.2.2.2', 'Réaliser des études de prédéfinition des projets urbains et étude de l’approvisionnement énergétique et des productions d’énergie (y compris potentiel de développement des énergies renouvelable) pour les sites à enjeux (friches qui se libèrent en centre-bourg ou ville, extensions urbaines prévues dans le document d’urbanisme, etc.)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.2.3', 'cae', '1.3.2.2.3', 'Utiliser des outils de modélisation climat-air-énergie (de type Analyse de Cycle Vie sur l''échelle quartier dès la conception) pour aider à la décision', '<p>Modélisation des ombres portées, modélisation de l’impact du projet sur la qualité de l’air, sur le ruissellement des eaux pluviales, sur le réchauffement urbain, quantification des gaz à effet de serre évités par le projet (GES-Opam du CEREMA), etc.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.3', 'cae', '1.3.2.3', 'Elaborer un document "cadre" pour systématiser la qualité environnementale des opérations maîtrisées par la collectivité', '', '', '', '', '', '', null, 15.0, 'bases');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.3.1', 'cae', '1.3.2.3.1', 'Définir des préconisations à l’échelle du quartier ou de la ville inspirées notamment des outils nationaux existants (cahiers thématiques AEU2, label national Ecoquartier, HQE-Performance quartier…)', '<p>Compacité des formes urbaines, orientation et implantation des bâtiments les uns par rapport aux autres, raccordement aux réseaux de chaleur alimentés majoritairement par des ENRR, autosuffisance énergétique, quartier sans voiture, végétalisation/déminéralisation des espaces (en limitant les allergènes potentiels), gestion de l’eau et rafraîchissement urbain, concertation citoyenne, etc.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.3.2', 'cae', '1.3.2.3.2', 'Définir des critères à l’échelle du bâtiment : BEPos, label E+/C-, Maison passive, BBC, Effinergie, Ecodom, HQE avec objectifs énergie ou air définis, label bâtiment biosourcé, chauffage très performant (flamme verte 7 étoiles pour le bois ou équivalent) ou valorisation des rejets thermiques, confort d''été, qualité de l''air intérieur...', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.3.3', 'cae', '1.3.2.3.3', 'Utiliser ce document comme base de discussions et de réflexions entre les acteurs et l''adapter/décliner dans différents documents de conception opérationnels des projets (plan masse, notice environnementale, cahier des charges, cahier de prescriptions...)', '', '', '', '', '', '', null, null, null);
