insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.5.1', 'cae', '1.3.2.5.1', 'Intègrer dans ses critères de choix des offres l''impact environnemental de la proposition : kWh économisé, émissions de CO2 ou émissions/exposition aux polluants atmosphériques, etc.', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.5.2', 'cae', '1.3.2.5.2', 'Utiliser des spécifications techniques pour fixer des objectifs de performances environnementales, de manière prescriptive, à l’échelle de la ville, du quartier et du bâtiment.', '<p>A l''échelle du quartier : AEU2, HQE Performance Quartier, etc.</p>
<p>A l''échelle du bâtiment : niveau passif ou à énergie positive -label E+/C- demandé pour les bâtiments neufs, label BBC rénovation pour les travaux de rénovation, obligation si pertinent d’installation de panneaux solaires sur les nouvelles toitures de bâtiment ou de raccordement à un réseau de chaleur, exiger si pertinent davantage de places de vélos dans les constructions neuves, réalisation d’abris à vélo extérieurs attenants au bâtiment principal, etc.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.5.3', 'cae', '1.3.2.5.3', 'Intègrer dans les critères de jugement des offres les références environnementales et les pratiques internes de l''entreprise candidate (expérience requise en HQE ou équivalent, label de performance énergétique, formation à l’AEU2, certification ISO 14001/9001 de l’entreprise, démarche d''éco-responsabilité interne...)', '', '', '', '', '', '', null, null, null);
