insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.7.5', 'cae', '1.3.2.7.5', 'Obtenir une reconnaissance nationale ou régionale d’une ou plusieurs opérations, certification d’une démarche (label économie circulaire, label éco-quartier…) ou de résultats obtenus, retombées presse ou visites de site par des acteurs extérieurs', '', '', '', '', '', '', null, null, null);
