insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.1.2.1.5', 'cae', '5.1.2.1.5', 'Réunir au sein du comité de pilotage des élus et personnes référentes dans des thématiques variées, non restreintes uniquement à l’énergie ou l’environnement (mobilité, aménagement, énergie, eau, assainissement, organisation interne, partenariats et coopération, finances, etc.) afin de traiter les questions climat air énergie de façon transversale', '', '', '', '', '', '', null, null, null);
