insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.1.2.2.3', 'cae', '5.1.2.2.3', 'Donner au comité de pilotage un rôle véritablement moteur pour la politique climat-air-énergie (décisions constructives et ambitieuses, lancement de nouvelles études ou actions…); Impulser via celui-ci une dynamique positive auprès des services', '', '', '', '', '', '', null, null, null);
