insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.1.4.1.1', 'cae', '5.1.4.1.1', 'Disposer d’un tableau de bord opérationnel pour le suivi du plan d’action', '<p>Pour chaque objectif opérationnel : un indicateur de suivi, qui soit mesurable, pertinent et compréhensible par le COPIL avec la fréquence de mise à jour et la source de données identifiées.</p>
<p>Pour chaque action : un responsable identifié, une planification pluriannuelle, un budget et un champ pour renseigner l’état d’avancement.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.1.4.1.2', 'cae', '5.1.4.1.2', 'Prévoir précisément les modalités du suivi', '<p>Fréquence annuelle de réunion à minima, date de mise à jour du tableau de bord, constitution de l’équipe de suivi, modalités de collecte des données nécessaires à l’actualisation du tableau de bord, etc.</p>
', '', '', '', '', '', null, null, null);
