insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.1.4.3.2', 'cae', '5.1.4.3.2', 'Publier les résultats et le suivi annuel', '<p>En interne, dans les instances concernées (commissions, conseil municipal, bureau, conseil communautaire..) et en externe sur le site de la collectivité, lors de réunions publiques, dans le rapport de DD, dans le rapport d’activité, etc.</p>
