insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.1.1.1.3', 'cae', '5.1.1.1.3', 'Désigner un chef de projet climat-air-énergie', '<p>En cas d’indisponibilité (arrêt maladie longue durée, maternité, départ…), son remplacement est une priorité pour éviter une absence de longue durée (ex : plus de 3 mois)</p>
', '', '', '', '', '', null, null, null);
