insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.1.1.4.2', 'cae', '5.1.1.4.2', 'Évaluer si le personnel et des référents spécialisés sont actifs et efficaces', '<p>Ils sont sollicités par les autres services, leurs collègues, leurs supérieurs hiérarchiques ou le grand public et ils répondent de manière réactive et pertinente. Si non, des actions pour réajuster l’organisation interne sont entreprises.</p>
', '', '', '', '', '', null, null, null);
