insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.1.1.1', 'cae', '5.2.1.1.1', 'Sensibiliser l’ensemble des élu.e.s à l’importance de piloter la politique climat-air-énergie dans ses aspects budgétaires, et leur présenter l’intérêt de réaliser une évaluation climat du budget', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.1.1.2', 'cae', '5.2.1.1.2', 'Nommer une équipe transversale pour prendre en charge les aspects budgétaires liés à la politique climat-air-énergie', '<p>Au minimum cette équipe comprend une personne en charge des finances et du budget et une personne en charge de la politique climat-air-énergie</p>
