insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.1.2.1', 'cae', '5.2.1.2.1', 'Faire correspondre à chaque étape de la construction de la politique climat-air-énergie un budget : action de sensibilisation, de communication, diagnostic, suivi, évaluation, programme d’actions. Ce budget de la politique climat-air-énergie est traduit en une trajectoire annuelle de dépenses.', '', '', '', '', '', '', null, null, null);
