insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.1.5.1', 'cae', '5.2.1.5.1', 'La collectivité étudie l’ensemble des sources de financement possibles pour les actions de sa politique climat-aire-énergie.', '<p>En particulier, la collectivité suit les dispositifs dédiés mis à disposition par l’Etat français et ses opérateurs (ADEME, ANCT, PIA, Banque des Territoires…), ainsi que les fonds européens (FEDER, LEADER, FSE, H2020, UIA, INTERREG, Life, Life IP, CEF, ...).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.1.5.2', 'cae', '5.2.1.5.2', 'La collectivité fait preuve d’innovation pour le financement des actions de sa politique climat-air-énergie (CEE, CPE, MDE et réaffectation de recettes, projets citoyens)', '<p>Vente de Certificat d’Economie d’Energie (suivi d''un indicateur en kWh/cumac valorisé par an), Contrat de Performance Energétique, identification des économies réalisées grâce à des actions de maîtrise de l’énergie ou d''efficacité et affectation de tout ou partie de ces &quot;recettes&quot; au budget de la politique énergétique, investissement participatif via l’émergence de projet citoyen, etc.</p>
', '', '', '', '', '', null, null, null);
